import { Component, OnInit } from '@angular/core';
import { Store } from '@ngrx/store';
import { Observable } from 'rxjs';
import { selectAllTodos$, Task, taskActions} from 'store';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {

 tasks$ : Observable<Task []>=this.store.select(selectAllTodos$);

  constructor(private store:Store) { }



  ngOnInit() {
    this.store.dispatch(taskActions.loadTasks());

  }

addTask(){
  let task :Task={id:1,text:"my first task",completed:true}
  this.store.dispatch(taskActions.addTask({task}))
}


}
<p>home works!</p>
<button (click)="addTask()">ADD</button>
<div *ngFor="let task of tasks$ |async">
    <div [innerHTML]="task.id">ID</div>
    <div [innerHTML]="task.text">text</div>
    <div [innerHTML]="task.completed">completed</div>


</div>
