import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class StoreService {

  constructor() { }
}
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'lib-store',
  template: `
    <p>
      store works!
    </p>
  `,
  styles: [
  ]
})
export class StoreComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }

}
import { NgModule } from '@angular/core';
import { StoreModule } from '@ngrx/store';
import { EffectsModule } from '@ngrx/effects';
import { TodoEffects } from './store/todo/todo.effects';
import { StoreComponent } from './store.component';

@NgModule({
  declarations: [StoreComponent],
  imports: [
  ],
  exports: [StoreComponent]
})
export class StoreLibModule { }
import { createAction, props } from '@ngrx/store';
import { Task } from './todo.state';



export const taskActions = {
  addTask: createAction('[Todo] Add Task', props<{ task: Task }>()),
  removeTask: createAction('[Todo] Remove Task', props<{ id: number }>()),
  loadTasks: createAction('[Todo] Load Tasks'),
  loadTasksSuccess: createAction('[Todo] Load Tasks success'),
  loadTasksFailure: createAction('[Todo] Load Tasks failure', props<{ error: any }>()),
  updateTask:createAction( '[Todo] Update Task',props<{ task: Task }>())
}
import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { exhaustMap, map, tap } from 'rxjs/operators';
import { taskActions} from './todo.actions';

@Injectable({
  providedIn: 'root'
})
export class TodoEffects {
  constructor(private actions$: Actions) {
    // alert('TodoEffects constructor called!!!');
    
    // נוסיף subscription לדיבאג
    this.actions$.subscribe(action => {
      console.log('Action received in effects:', action);
    });
  }

  loadTodos$ = createEffect(() => {
    // console.log('Setting up loadTodos$ effect');
    return this.actions$.pipe(
      // tap(action => console.log('Action in pipe:', action)),
      ofType(taskActions.loadTasks),
      // tap(() => alert('loadTodos action caught')),
      map(() => taskActions.loadTasksSuccess())
    );
  });

  addTask$ = createEffect(() => {
    // console.log('Setting up addTask$ effect');
    return this.actions$.pipe(
      // tap(action => console.log('Action in pipe:', action)),
      ofType(taskActions.addTask),
      // tap(() => alert('addTask action caught')),
        map(_ => taskActions.loadTasksSuccess())
      )})  
     
removeTask$ = createEffect(() => {
  // console.log('Setting up addTask$ effect');
  return this.actions$.pipe(
    // tap(action => console.log('Action in pipe:', action)),
    ofType(taskActions.removeTask),
    // tap(() => alert('addTask action caught')),
      map(_ => taskActions.loadTasksSuccess())
    )})  
updateTask$ = createEffect(() => {
  return this.actions$.pipe(
    ofType(taskActions.updateTask),
      map(_ => taskActions.loadTasksSuccess())
    )})  
} 
export interface Task {
  id: number;
  text: string;
  completed?: boolean;
}

export interface TodoState {
  tasks: Task[];

}

export const initialState: TodoState = {
  tasks:[]=[],

}; 
import { createReducer, on } from '@ngrx/store';
import { TodoState } from './todo.state';
import { taskActions} from './todo.actions';
import { initialState } from './todo.state';

export const todoReducer = createReducer(
initialState, 
  on(taskActions.loadTasks, state => ({
    ...state,
    loading: true
  })),
  on(taskActions.loadTasksSuccess, (state) => ({
    ...state,
    loading: false,
  })),
  on(taskActions.loadTasksFailure, (state, { error }) => ({
    ...state,
    loading: false,
    error
  })),
  on(taskActions.addTask, (state, { task }) => ({
    ...state,
    tasks: [...state.tasks, task] // דחיפת הנתון החדש למערך

   })),
  on(taskActions.removeTask, (state, { id }) => ({
    ...state,
    tasks: state.tasks.filter(task => task.id !== id)
  }))
); 
import { createFeatureSelector, createSelector, MemoizedSelector } from '@ngrx/store';
import { TodoState } from './todo.state';
import { Task } from './todo.state';

export const taskFeatureSelector: MemoizedSelector<object, TodoState>  = createFeatureSelector<TodoState>('todos');

export const selectAllTodos$ : MemoizedSelector<object, Task[]> =
createSelector(
   taskFeatureSelector,
     ({tasks}) => tasks );




// export const selectTodoLoading = createSelector(
//   selectTodoState,
//   (state: TodoState) => state
// ); 
