import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class StoreService {

  constructor() { }
}
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'lib-store',
  template: `
    <p>
      store works!
    </p>
  `,
  styles: [
  ]
})
export class StoreComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }

}
import { createAction, props } from '@ngrx/store';



export const addTask = createAction('[Todo] Add Task', props<{ task: string }>());
export const removeTask = createAction('[Todo] Remove Task', props<{ id: number }>())
export const loadTasks = createAction('[Todo] Load Tasks');
export const LoadTasksSuccessAction = createAction('[Todo] Load Tasks success')
export const LoadTasksFailureAction = createAction('[Todo] Load Tasks failure', props<{ error: any }>())

import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { map, tap } from 'rxjs/operators';
import * as TodoActions from './todo.actions';

@Injectable({
  providedIn: 'root'
})
export class TodoEffects {
  constructor(private actions$: Actions) {
    console.log('TodoEffects constructor called!!!');
    
    // נוסיף subscription לדיבאג
    this.actions$.subscribe(action => {
      console.log('Action received in effects:', action);
    });
  }

  loadTodos$ = createEffect(() => {
    console.log('Setting up loadTodos$ effect');
    return this.actions$.pipe(
      tap(action => console.log('Action in pipe:', action)),
      ofType(TodoActions.loadTasks),
      tap(() => console.log('loadTodos action caught')),
      map(() => TodoActions.LoadTasksSuccessAction())
    );
  });
} 
import { NgModule } from '@angular/core';
import { StoreModule } from '@ngrx/store';
import { EffectsModule } from '@ngrx/effects';
import { TodoEffects } from './store/todo.effects';

@NgModule({
  imports: [
    StoreModule.forRoot({}),
    EffectsModule.forRoot([TodoEffects])
  ],
  exports: [
    StoreModule,
    EffectsModule
  ],
  providers:[TodoEffects]
})
export class StoreLibModule { }
export interface Todo {
  id: number;
  text: string;
  completed: boolean;
}

export interface TodoState {
  todos: Todo[];
  loading: boolean;
  error: string | null;
}

export const initialState: TodoState = {
  todos: [],
  loading: false,
  error: null
}; 
import { createReducer, on } from '@ngrx/store';
import { TodoState, initialState } from './todo.state';
import * as TodoActions  from './todo.actions';

export const todoReducer = createReducer(
  initialState,
  on(TodoActions.loadTasks, state => ({
    ...state,
    loading: true
  })),
  on(TodoActions.LoadTasksSuccessAction, (state) => ({
    ...state,
    loading: false,
  })),
  on(TodoActions.LoadTasksFailureAction, (state, { error }) => ({
    ...state,
    loading: false,
    error
  }))
); 
import { createFeatureSelector, createSelector } from '@ngrx/store';
import { TodoState } from './todo.state';

export const selectTodoState = createFeatureSelector<TodoState>('todos');

export const selectAllTodos = createSelector(
  selectTodoState,
  (state: TodoState) => state.todos
);

export const selectTodoLoading = createSelector(
  selectTodoState,
  (state: TodoState) => state.loading
); 
/*
 * Public API Surface of store
 */

export * from './lib/store.service';
export * from './lib/store.component';
export * from './lib/store.module';

export * from './lib/store/todo.actions';
export * from './lib/store/todo.effects'
export * from './lib/store/todo.reducer'
export * from './lib/store/todo.selectors'
export * from './lib/store/todo.state'
/**
 * Generated bundle index. Do not edit.
 */

export * from './public-api';
