export interface Task {
  id: number;
  text: string;
  completed?: boolean;
}

export interface TodoState {
  tasks: Task[];
  loading: boolean;
  error: string | null;
}

export const initialState: TodoState = {
  tasks: [],
  loading: false,
  error: null
};
