import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { Store } from '@ngrx/store';
import { concatMap, interval, map, mergeMap, Observable, of, switchMap, take, tap } from 'rxjs';
import { selectAllTodos$, Task, taskActions, TodoState} from 'store';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {

 tasks$ : Observable< any>=this.store.select(selectAllTodos$).pipe( );

  id: number=0;
  completed: boolean = true;
  form: FormGroup;
  editable: boolean=false;
  newText: string ='';

  constructor(private store:Store) { }



  ngOnInit() {   
    const source$ = of(1, 2, 3);

    source$.pipe(
      map(value => value * 2)
    ).subscribe(console.log); // ◊§◊ú◊ò: 2, 4, 6

    source$.pipe(
      tap(val=>console.log(val)),
      mergeMap(value => interval(500).pipe(take(2)))
    ).subscribe(console.log); // ◊§◊ú◊ò: ◊û◊°◊§◊®◊ô◊ù ◊û-2 ◊¢◊®◊ï◊¶◊ô◊ù ◊ë◊û◊ß◊ë◊ô◊ú

    source$.pipe(
      switchMap(value => interval(500).pipe(take(2)))
    ).subscribe(console.log); // ◊§◊ú◊ò: ◊¢◊®◊õ◊ô◊ù ◊û-Observable ◊î◊ê◊ó◊®◊ï◊ü ◊ë◊ú◊ë◊ì

    source$.pipe(
      concatMap(value => interval(500).pipe(take(2)))
    ).subscribe(console.log); // ◊§◊ú◊ò: ◊¢◊®◊õ◊ô◊ù ◊ë◊°◊ì◊® ◊î◊û◊ß◊ï◊®◊ô


    this.initForm()
    this.store.dispatch(taskActions.loadTasks());
  }

addTask(){
  this.id++
  this.completed=!this.completed
  console.log(this.form);

  let task :Task={id:this.id,text:this.form.controls.taskValue.value}

  this.store.dispatch(taskActions.addTask({task}))
  this.initForm()
}

removeTask(id:number){
  this.store.dispatch(taskActions.removeTask({id}))

}
updateTask(){
this.editable=true;

}



updateTaskText(event:Event){
  const target = event.target as HTMLElement;
    this.newText = target.innerText;
}

okUpdateTask(id:number){
  this.editable=false;
  alert(this.newText)
    this.store.dispatch(taskActions.updateTask({id , newText:this.newText}))

}

initForm(){
this.form=new FormGroup({  
  taskValue : new FormControl(''),
  completed : new FormControl('')
})
}
}
<p>home works!</p>
     <form [formGroup]="form">
<input type="text" formControlName="taskValue" placeholder="Enter your task">
<input type="text" formControlName="completed" placeholder="Enter if you completed">
 </form>
<button (click)="addTask()">ADD</button>
<div *ngFor="let task of tasks$ | async">
    <!-- <span [innerText]="task.id">ID</span> -->
    <div class="border border-primary rounded p-3">
        <span  [attr.contenteditable]="editable" [innerText]="task.text" (input)="updateTaskText($event)">text</span>
    <span [innerText]="task.completed" *ngIf="task.completed">completed</span>
    <button (click)="removeTask(task.id)" class="text-danger">    ‚ùå </button>
    <button (click)="updateTask()" > üñä </button>
    <button (click)="okUpdateTask(task.id)"  >‚úÖ</button>


</div>

</div>
