import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css']
})
export class CheckoutComponent implements OnInit{



  ngOnInit() {
  }
  addresses = [
    { name: 'דני כהן', street: 'הרצל 1', city: 'תל אביב' },
    { name: 'נועה לוי', street: 'אלנבי 12', city: 'חיפה' }
  ];
  selectedAddress: any = null;

  paymentMethods = ['כרטיס אשראי', 'פייפאל', 'ביט'];
  selectedPaymentMethod = '';

  cartItems = [
    { name: 'מוצר א', quantity: 2, price: 50 },
    { name: 'מוצר ב', quantity: 1, price: 30 }
  ];

  newAddress = { name: '', street: '', city: '' };
  showAddressForm = false;

  toggleAddressForm() {
    this.showAddressForm = !this.showAddressForm;
  }

  addAddress() {
    this.addresses.push({ ...this.newAddress });
    this.newAddress = { name: '', street: '', city: '' };
    this.showAddressForm = false;
  }

  selectAddress(address: any) {
    this.selectedAddress = address;
  }

  calculateTotal() {
    return this.cartItems.reduce((total, item) => total + item.quantity * item.price, 0);
  }

  completeCheckout() {
    if (!this.selectedAddress) {
      alert('נא לבחור כתובת למשלוח');
      return;
    }
    if (!this.selectedPaymentMethod) {
      alert('נא לבחור אמצעי תשלום');
      return;
    }
    alert('ההזמנה הושלמה בהצלחה!');
  }
}
