import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class StoreService {

  constructor() { }
}

import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'lib-store',
  template: `
    <p>
      store works!
    </p>
  `,
  styles: [
  ]
})
export class StoreComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }

}

import { createAction, props } from '@ngrx/store';
import { Todo } from './todo.state';



export const loadTodos = createAction('[Todo] Load Todos');
export const loadTodosSuccess = createAction(
  '[Todo] Load Todos Success',
  props<{ todos: Todo[] }>()
);
export const loadTodosFailure = createAction(
  '[Todo] Load Todos Failure',
  props<{ error: string }>()
);

import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { map, tap } from 'rxjs/operators';
import * as TodoActions from './todo.actions';

@Injectable()
export class TodoEffects {
  constructor(private actions$: Actions) {
    console.log('TodoEffects constructor called');
  }

  loadTodos$ = createEffect(() => this.actions$.pipe(
    ofType(TodoActions.loadTodos),
    tap(() => console.log('Action received')),
    map(() => TodoActions.loadTodosSuccess({ todos: [] }))
  ));
}

import { NgModule } from '@angular/core';
import { StoreComponent } from './store.component';
import { EffectsModule } from '@ngrx/effects';
import { TodoEffects } from './store/todo.effects';



@NgModule({
  declarations: [
    StoreComponent
  ],
  imports: [
    EffectsModule.forRoot([TodoEffects]),
  ],
  exports: [
    StoreComponent
  ]
})
export class StoreModule { }

export interface Todo {
  id: number;
  text: string;
  completed: boolean;
}

export interface TodoState {
  todos: Todo[];
  loading: boolean;
  error: string | null;
}

export const initialState: TodoState = {
  todos: [],
  loading: false,
  error: null
};

import { createReducer, on } from '@ngrx/store';
import { TodoState, initialState } from './todo.state';
import * as TodoActions  from './todo.actions';

export const todoReducer = createReducer(
  initialState,
  on(TodoActions.loadTodos, state => ({
    ...state,
    loading: true
  })),
  on(TodoActions.loadTodosSuccess, (state, { todos }) => ({
    ...state,
    loading: false,
    todos
  })),
  on(TodoActions.loadTodosFailure, (state, { error }) => ({
    ...state,
    loading: false,
    error
  }))
);

import { createFeatureSelector, createSelector } from '@ngrx/store';
import { TodoState } from './todo.state';

export const selectTodoState = createFeatureSelector<TodoState>('todos');

export const selectAllTodos = createSelector(
  selectTodoState,
  (state: TodoState) => state.todos
);

export const selectTodoLoading = createSelector(
  selectTodoState,
  (state: TodoState) => state.loading
);

/*
 * Public API Surface of store
 */

export * from './lib/store.service';
export * from './lib/store.component';
export * from './lib/store.module';

export * from './lib/store/todo.actions';
export * from './lib/store/todo.effects'
export * from './lib/store/todo.reducer'
export * from './lib/store/todo.selectors'
export * from './lib/store/todo.state'

/**
 * Generated bundle index. Do not edit.
 */

export * from './public-api';
